import React from "react";
import Slider from "react-animated-slider";
import "react-animated-slider/build/horizontal.css";
import "normalize.css/normalize.css";
import "./SliderAnimations.css";
import "./Slider.css";
import esummit from './Images/esummit.jpg';
import pitchers from './Images/pitchers.jpg';
import startupexpo from './Images/startupexpo.jpg';
import vlweekend from './Images/vlweekend.jpg';
import Popup from "reactjs-popup";





const content = [
  {
    title: "E-SUMMIT",
    description:
      "Aenean eu leo quam. Pellentesque ornare sem lacinia quam venenatis vestibulum. Fusce dapibus, tellus ac cursus commodo, tortor mauris condimentum nibh, ut fermentum massa justo sit amet risus. Cras justo odio, dapibus ac facilisis.",
    button: "Explore",
    buttonurl: "www.facebbok.com",
    image: esummit,
  },
  {
    title: "VL WEEKEND",
    description:
      "Nullam id dolor id nibh ultricies vehicula ut id elit. Cras mattis consectetur purus sit amet fermentum. Morbi leo risus, porta ac consectetur ac, vestibulum at eros. Donec sed odio dui.",
    button: "Know More",
    image: vlweekend,
  },
  {
    title: "PITCHERS",
    description:
        "",
    button: "Know More",
    image: pitchers,
  },
  {
    title: "STARTUP-EXPO",
    description: 
        "",
    button: "Explore",
    image: startupexpo,
    contentPopup: "loremsjDncsjdcndsjcsjsnxsjcndsnckjcnsdjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjj",
  }
];

const SlideEvents = () => (
  <div>
    <Slider  autoplay={1500}className="slider-wrapper">
      {content.map((item, index) => (
        <div
          key={index}
          className="slider-content"
          style={{ background: `url('${item.image}') no-repeat center center` }}
        >
          <div className="inner">
            <h1>{item.title}</h1>
            <p>{item.description}</p>
            <Popup trigger={<button>{item.button}</button>} modal>
            <div>{item.contentPopup}</div>
            </Popup>
          </div>
        </div>
      ))}
    </Slider>
  </div>
);

{close => (
    <div className="modal">
      <a className="close" onClick={close}>
        &times;
      </a>
      <div className="header"> Modal Title </div>
      <div className="content">
        {" "}
        {}
      </div>
      <div className="actions">
        <button
          className="button"
          onClick={() => {
            console.log("modal closed ");
            close();
          }}
        >
          close modal
        </button>
      </div>
    </div>
  )}


export default SlideEvents;
